import React, { useState, useEffect } from "react";
import { Pie } from "react-chartjs-2";
import "chart.js/auto";
import "./AdminApp.css";

function AdminApp() {
  const [sessions, setSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [sessionDates, setSessionDates] = useState({});

  useEffect(() => {
    fetchSessions();
  }, []);

  const fetchSessions = async () => {
    setIsLoading(true);
    try {
      const response = await fetch("http://localhost:5000/sessions");
      if (!response.ok) {
        throw new Error("Failed to fetch sessions");
      }
      const data = await response.json();
      setSessions(data.sessions || []);
      setSessionDates(data.sessionDates || {});
    } catch (error) {
      console.error("Error fetching sessions:", error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSessionClick = async (sessionId) => {
    setSelectedSession(sessionId);
    try {
      const response = await fetch(
        `http://localhost:5000/analyze/${sessionId}`
      );
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();
      setAnalysisData(data);
    } catch (error) {
      console.error("Error analyzing session:", error);
    }
  };

  const handleBackToSessions = () => {
    setSelectedSession(null);
    setAnalysisData(null);
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const renderSessionList = () => (
    <div className="sessions-list-container">
      <h3>Session List</h3>
      {isLoading ? (
        <p>Loading sessions...</p>
      ) : (
        <table className="session-table">
          <thead>
            <tr>
              <th>Username</th>
              <th>Session ID</th>
              <th>Date & Time</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {sessions.map((user) =>
              user.sessions.map((session, index) => (
                <tr key={session.sessionId}>
                  {index === 0 && (
                    <td rowSpan={user.sessions.length}>{user.username}</td>
                  )}
                  <td>{session.sessionId}</td>
                  <td>{formatDate(session.createdAt)}</td>
                  <td>
                    <button
                      onClick={() => handleSessionClick(session.sessionId)}
                      className="get-analysis-btn"
                    >
                      Get Analysis
                    </button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      )}
    </div>
  );

  const renderPieChart = () => {
    if (!analysisData?.overallAnalysis) return null;

    const emotions = analysisData.overallAnalysis.emotions;
    const emotionLabelsWithEmojis = {
      happy: "😊 Happy",
      sad: "😢 Sad",
      angry: "😠 Angry",
      surprised: "😮 Surprised",
      fearful: "😨 Fearful",
      neutral: "😐 Neutral",
      disgust: "🤢 Disgust",
      fear: "😨 Fear",
      surprise: "😯 Surprise",
    };

    const labelsWithEmojis = Object.keys(emotions).map(
      (emotion) => emotionLabelsWithEmojis[emotion] || emotion
    );

    const data = {
      labels: labelsWithEmojis,
      datasets: [
        {
          label: "Emotion Analysis (%)",
          data: Object.values(emotions).map((v) => parseFloat(v)),
          backgroundColor: [
            "#FF6384",
            "#36A2EB",
            "#FFCE56",
            "#4BC0C0",
            "#9966FF",
            "#FF9F40",
            "#CFCFCF",
            "#D4A5A5",
            "#C8E6C9",
          ],
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: "top",
          labels: {
            font: {
              size: 14,
            },
          },
        },
      },
    };

    return (
      <div className="pie-chart-container">
        <h3>Overall Emotion Analysis</h3>
        <Pie data={data} options={options} />
      </div>
    );
  };

  const renderTable = () => {
    if (!analysisData?.imageAnalyses) return null;

    return (
      <div className="table-container">
        <table className="analysis-table">
          <thead>
            <tr>
              <th>Webcam Capture</th>
              <th>Game Screenshot</th>
              <th>Analysis</th>
            </tr>
          </thead>
          <tbody>
            {analysisData.imageAnalyses.map((analysis, index) => (
              <tr key={index}>
                <td>
                  <img
                    src={`http://localhost:5000/uploads/webcam_images/${selectedSession}/${analysis.imagePath}`}
                    alt={`Webcam ${index}`}
                    className="webcam-image"
                  />
                </td>
                <td>
                  <img
                    src={`http://localhost:5000/uploads/game_screenshots/${selectedSession}/screenshot_${index}.png`}
                    alt={`Game Screenshot ${index}`}
                    className="screenshot-image"
                  />
                </td>
                <td>
                  {Object.entries(analysis.emotions).map(([emotion, value]) => (
                    <p key={emotion}>
                      {emotion}: {parseFloat(value).toFixed(2)}%
                    </p>
                  ))}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="admin-app-container">
      <h2>Admin Dashboard</h2>
      {selectedSession ? (
        <div className="analysis-view">
          <h3>Analysis for Session: {selectedSession}</h3>
          {renderPieChart()}
          {renderTable()}
          <button onClick={handleBackToSessions} className="back-to-sessions-btn">
            Back to Sessions
          </button>
        </div>
      ) : (
        renderSessionList()
      )}
    </div>
  );
}

