import React, { useState, useEffect } from "react";
import { Pie } from "react-chartjs-2";
import "chart.js/auto";
import "./AdminApp.css";

function AdminApp() {
  const [sessions, setSessions] = useState([]);
  const [selectedSession, setSelectedSession] = useState(null);
  const [analysisData, setAnalysisData] = useState(null);

  useEffect(() => {
    fetchSessions();
  }, []);

  const fetchSessions = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/sessions");
      if (!response.ok) {
        throw new Error("Network response was not ok");
      }
      const data = await response.json();

      // Group sessions data by user
      const groupedSessions = data.map((group) => ({
        username: group.username,
        sessions: group.sessions,
      }));

      setSessions(groupedSessions);
    } catch (error) {
      console.error("Error fetching sessions:", error);
    }
  };

  const handleSessionClick = async (sessionId) => {
    setSelectedSession(sessionId);
    try {
      const response = await fetch(
        `http://localhost:5000/analyze/${sessionId}`
      );
      if (!response.ok) {
        if (response.status === 404) {
          setAnalysisData({
            imageAnalyses: [],
            overallAnalysis: { emotions: {} },
          });
        } else {
          throw new Error("Network response was not ok");
        }
      }
      const data = await response.json();
      setAnalysisData(data);
    } catch (error) {
      console.error("Error analyzing session:", error);
    }
  };

  const handleBackToSessions = () => {
    setSelectedSession(null);
    setAnalysisData(null);
  };

  const formatDate = (dateString) => {
    if (!dateString) return "";
    const date = new Date(dateString);
    return date.toLocaleString();
  };

  const renderSessionsTable = () => (
    <div className="sessions-table-container">
      <h3>Sessions Dashboard</h3>
      <table className="sessions-table">
        <thead>
          <tr>
            <th>Username</th>
            <th>Session ID</th>
            <th>Created At</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {sessions.map((group) =>
            group.sessions.map((session, index) => (
              <tr key={session.sessionId}>
                {index === 0 && (
                  <td rowSpan={group.sessions.length}>{group.username}</td>
                )}
                <td>{session.sessionId}</td>
                <td>{formatDate(session.createdAt)}</td>
                <td>
                  <button
                    onClick={() => handleSessionClick(session.sessionId)}
                    className="action-btn"
                  >
                    Analyze
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );

  const renderPieChart = () => {
    if (
      !analysisData ||
      !analysisData.overallAnalysis ||
      Object.keys(analysisData.overallAnalysis.emotions).length === 0
    )
      return null;

    const emotions = analysisData.overallAnalysis.emotions;
    const emotionLabelsWithEmojis = {
      happy: "ðŸ˜Š Happy",
      sad: "ðŸ˜¢ Sad",
      angry: "ðŸ˜  Angry",
      surprised: "ðŸ˜® Surprised",
      fearful: "ðŸ˜¨ Fearful",
      neutral: "ðŸ˜ Neutral",
    };

    const data = {
      labels: Object.keys(emotions).map(
        (emotion) => emotionLabelsWithEmojis[emotion] || emotion
      ),
      datasets: [
        {
          data: Object.values(emotions).map((v) => parseFloat(v)),
          backgroundColor: [
            "#FF6384",
            "#36A2EB",
            "#FFCE56",
            "#4BC0C0",
            "#9966FF",
            "#FF9F40",
          ],
        },
      ],
    };

    const options = {
      responsive: true,
      plugins: {
        legend: {
          position: "top",
        },
      },
    };

    return (
      <div className="chart-container">
        <Pie data={data} options={options} />
      </div>
    );
  };

  const renderAnalysisTable = () => {
    if (!analysisData || analysisData.imageAnalyses.length === 0) return null;

    return (
      <div className="analysis-table-container">
        <table className="analysis-table">
          <thead>
            <tr>
              <th>Webcam Capture</th>
              <th>Game Screenshot</th>
              <th>Analysis</th>
            </tr>
          </thead>
          <tbody>
            {analysisData.imageAnalyses.map((analysis, index) => (
              <tr key={index}>
                <td>
                  <img
                    src={`http://localhost:5000/uploads/webcam_images/${selectedSession}/${analysis.imagePath}`}
                    alt={`Webcam ${index}`}
                    className="image-preview"
                  />
                </td>
                <td>
                  <img
                    src={`http://localhost:5000/uploads/screenshots/${selectedSession}/${analysis.imagePath}`}
                    alt={`Screenshot ${index}`}
                    className="image-preview"
                  />
                </td>
                <td>
                  {Object.entries(analysis.emotions).map(([emotion, value]) => (
                    <div key={emotion}>
                      {emotion}: {parseFloat(value).toFixed(2)}%
                    </div>
                  ))}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="admin-dashboard">
      <h2>Admin Dashboard</h2>
      {selectedSession ? (
        <>
          {renderPieChart()}
          {renderAnalysisTable()}
          <button
            onClick={handleBackToSessions}
            className="back-btn"
          >
            Back to Sessions
          </button>
        </>
      ) : (
        renderSessionsTable()
      )}
    </div>
  );
}

export default AdminApp;
